// src/pages/ChallengeList.js
import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import API from '../api';

export default function ChallengeList() {
    const [challenges, setChallenges] = useState([]);
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(true);
    const navigate = useNavigate();
    const location = useLocation();

    const loadChallenges = async () => {
        setIsLoading(true);
        try {
            const res = await API.get('/api/challenges');
            setChallenges(res.data);
            setError('');
        } catch (err) {
            if (err.response?.status === 401) {
                setError('Please log in again.');
                setTimeout(() => navigate('/login'), 1500);
            } else {
                setError('Failed to load challenges.');
            }
        } finally {
            setIsLoading(false);
        }
    };

    // whenever we land on /challenges, fetch fresh data
    useEffect(() => {
        if (location.pathname === '/challenges') {
            loadChallenges();
        }
    }, [location.pathname]);

    if (error) {
        return (
            <div className="dashboard-container">
                <p className="error-message">{error}</p>
            </div>
        );
    }

    if (isLoading) {
        return (
            <div className="dashboard-container">
                <p>Loading challenges…</p>
            </div>
        );
    }

    return (
        <div className="dashboard-container">
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <h1 className="dashboard-header">Challenges</h1>
                <button className="enter-btn" onClick={loadChallenges} disabled={isLoading}>
                    {isLoading ? 'Refreshing…' : 'Refresh'}
                </button>
            </div>
            <div className="dashboard-content">
                {challenges.map(c => (
                    <div key={c.id} className="dashboard-card">
                        <h2>
                            {c.title}
                            {c.completed && <span className="completed-badge">Completed</span>}
                        </h2>
                        <p>Difficulty: {c.difficulty}</p>
                        <p>XP Reward: {c.xp_reward}</p>

                        {c.completed ? (
                            <button className="enter-btn" disabled>
                                Completed
                            </button>
                        ) : (
                            <Link to={`/challenges/${c.id}`}>
                                <button className="enter-btn">Start</button>
                            </Link>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}
