import React, { useState, useEffect } from 'react';
import {
    Routes,
    Route,
    Navigate,
    Link,
    useLocation,
    useNavigate
} from 'react-router-dom';

import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Home from './pages/Home';
import ChallengeList from './pages/ChallengeList';
import ChallengeDetail from './pages/ChallengeDetail';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isLightMode, setIsLightMode] = useState(false);
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) setIsLoggedIn(true);

        const savedMode = localStorage.getItem('theme');
        if (savedMode === 'light') {
            document.body.classList.add('light-mode');
            setIsLightMode(true);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        navigate('/login', { replace: true });
    };

    const toggleLightMode = () => {
        document.body.classList.toggle('light-mode');
        const newMode = document.body.classList.contains('light-mode');
        setIsLightMode(newMode);
        localStorage.setItem('theme', newMode ? 'light' : 'dark');
    };

    // small button style
    const navBtnStyle = {
        padding: '6px 12px',
        fontSize: '14px',
        marginLeft: '0.5rem'
    };

    return (
        <>
            {/* Centered Logo */}
            <div className="header-logo">
                <Link to="/">
                    <img
                        src={
                            isLightMode
                                ? `${process.env.PUBLIC_URL}/media/ninjatp.png`
                                : `${process.env.PUBLIC_URL}/media/ninjatpd.png`
                        }
                        alt="Home"
                        className="ninja-logo"
                    />
                </Link>
            </div>

            {/* Navbar */}
            <div
                className="navbar"
                style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                    padding: '0 1rem'
                }}
            >
                {/* Left: Light/Dark Toggle */}
                <button
                    className="light-mode-btn"
                    onClick={toggleLightMode}
                    style={navBtnStyle}
                >
                    {isLightMode ? 'Dark Mode' : 'Light Mode'}
                </button>

                {/* Right: Dashboard / Challenges / Logout */}
                {isLoggedIn && (
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                        <Link to="/dashboard" style={{ textDecoration: 'none' }}>
                            <button className="enter-btn" style={navBtnStyle}>
                                Dashboard
                            </button>
                        </Link>
                        <Link to="/challenges" style={{ textDecoration: 'none' }}>
                            <button className="enter-btn" style={navBtnStyle}>
                                Challenges
                            </button>
                        </Link>
                        <button
                            className="logout-btn"
                            onClick={handleLogout}
                            style={navBtnStyle}
                        >
                            Logout
                        </button>
                    </div>
                )}
            </div>

            {/* Routes */}
            <Routes>
                <Route path="/" element={<Home />} />

                <Route
                    path="/login"
                    element={
                        isLoggedIn
                            ? <Navigate to="/dashboard" replace />
                            : <Login setIsLoggedIn={setIsLoggedIn} />
                    }
                />

                <Route
                    path="/register"
                    element={
                        isLoggedIn
                            ? <Navigate to="/dashboard" replace />
                            : <Register />
                    }
                />

                <Route
                    path="/dashboard"
                    element={
                        isLoggedIn
                            ? <Dashboard handleLogout={handleLogout} />
                            : <Navigate to="/login" replace />
                    }
                />

                <Route
                    path="/challenges"
                    element={
                        isLoggedIn
                            ? <ChallengeList />
                            : <Navigate to="/login" replace />
                    }
                />

                <Route
                    path="/challenges/:id"
                    element={
                        isLoggedIn
                            ? <ChallengeDetail />
                            : <Navigate to="/login" replace />
                    }
                />

                <Route
                    path="*"
                    element={
                        isLoggedIn
                            ? <Navigate to="/dashboard" replace />
                            : <Navigate to="/login" replace />
                    }
                />
            </Routes>
        </>
    );
};

export default App;
