import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, Link, useLocation } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Home from './pages/Home';
import ChallengeList from './pages/ChallengeList';
import ChallengeDetail from './pages/ChallengeDetail';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isLightMode, setIsLightMode] = useState(false);
    const location = useLocation();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) setIsLoggedIn(true);

        const savedMode = localStorage.getItem('theme');
        if (savedMode === 'light') {
            document.body.classList.add('light-mode');
            setIsLightMode(true);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
    };

    const toggleLightMode = () => {
        document.body.classList.toggle('light-mode');
        const newMode = document.body.classList.contains('light-mode');
        setIsLightMode(newMode);
        localStorage.setItem('theme', newMode ? 'light' : 'dark');
    };

    return (
        <>
            <div className="header-logo">
                <Link to="/">
                    <img
                        src={
                            isLightMode
                                ? `${process.env.PUBLIC_URL}/media/ninjatp.png`
                                : `${process.env.PUBLIC_URL}/media/ninjatpd.png`
                        }
                        alt="Home"
                        className="ninja-logo"
                    />
                </Link>
            </div>

            <div className="navbar">
                <button
                    className="light-mode-btn"
                    onClick={toggleLightMode}
                    style={{
                        backgroundColor: isLightMode ? '#ffffff' : '#000000',
                        color: isLightMode ? '#000000' : '#ffffff',
                    }}
                >
                    {isLightMode ? 'Dark Mode' : 'Light Mode'}
                </button>

                {isLoggedIn && (
                    <Link to="/challenges" className="nav-link" style={{ margin: '0 1rem' }}>
                        Challenges
                    </Link>
                )}

                {location.pathname === '/dashboard' && isLoggedIn && (
                    <button
                        className="logout-btn"
                        onClick={handleLogout}
                        style={{ backgroundColor: 'red', color: 'white' }}
                    >
                        Logout
                    </button>
                )}
            </div>

            <Routes>
                <Route path="/" element={<Home />} />

                <Route
                    path="/login"
                    element={
                        isLoggedIn
                            ? <Navigate to="/dashboard" replace />
                            : <Login setIsLoggedIn={setIsLoggedIn} />
                    }
                />

                <Route
                    path="/register"
                    element={
                        isLoggedIn
                            ? <Navigate to="/dashboard" replace />
                            : <Register />
                    }
                />

                <Route
                    path="/dashboard"
                    element={
                        isLoggedIn
                            ? <Dashboard handleLogout={handleLogout} />
                            : <Navigate to="/login" replace />
                    }
                />

                <Route
                    path="/challenges"
                    element={
                        isLoggedIn
                            ? <ChallengeList />
                            : <Navigate to="/login" replace />
                    }
                />

                <Route
                    path="/challenges/:id"
                    element={
                        isLoggedIn
                            ? <ChallengeDetail />
                            : <Navigate to="/login" replace />
                    }
                />

                <Route
                    path="*"
                    element={
                        isLoggedIn
                            ? <Navigate to="/dashboard" replace />
                            : <Navigate to="/login" replace />
                    }
                />
            </Routes>
        </>
    );
};

export default App;
