import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useLocation, Link } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
    };

    const location = useLocation(); // useLocation is safe here because Router is higher in the hierarchy

    return (
        <>
            <nav className="navbar">
                <div className="nav-links">
                    {location.pathname !== '/' && <Link to="/">Home</Link>}
                    {!isLoggedIn && location.pathname !== '/login' && <Link to="/login">Login</Link>}
                    {!isLoggedIn && location.pathname !== '/register' && <Link to="/register">Register</Link>}
                    {isLoggedIn && location.pathname !== '/dashboard' && <Link to="/dashboard">Dashboard</Link>}
                </div>
                {isLoggedIn && (
                    <button className="logout-btn" onClick={handleLogout}>
                        Logout
                    </button>
                )}
            </nav>

            <Routes>
                <Route
                    path="/"
                    element={isLoggedIn ? <Navigate to="/dashboard" /> : <Login setIsLoggedIn={setIsLoggedIn} />}
                />
                <Route
                    path="/login"
                    element={isLoggedIn ? <Navigate to="/dashboard" /> : <Login setIsLoggedIn={setIsLoggedIn} />}
                />
                <Route path="/register" element={isLoggedIn ? <Navigate to="/dashboard" /> : <Register />} />
                <Route path="/dashboard" element={isLoggedIn ? <Dashboard /> : <Navigate to="/login" />} />
            </Routes>
        </>
    );
};

export default App;
