// src/pages/ChallengeDetail.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import API from '../api';

export default function ChallengeDetail() {
    const { id } = useParams();
    const [challenge, setChallenge] = useState(null);
    const [output, setOutput] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        API.get(`/challenges/${id}`)
            .then(res => setChallenge(res.data))
            .catch(() => setMessage('Could not load challenge.'));
    }, [id]);

    const submit = e => {
        e.preventDefault();
        setMessage('Submitting…');
        API.post('/submit-challenge', {
            challenge_id: Number(id),
            user_output: output
        })
            .then(res => {
                setMessage(`${res.data.message} XP: ${res.data.xp}, Rank: ${res.data.rank}`);
            })
            .catch(err => {
                setMessage(err.response?.data?.message || 'Error submitting.');
            });
    };

    if (!challenge) return <p>{message || 'Loading…'}</p>;

    return (
        <div className="challenge-detail">
            <h1>{challenge.title}</h1>
            <p><strong>Difficulty:</strong> {challenge.difficulty}</p>
            <p><strong>XP:</strong> {challenge.xp_reward}</p>
            <p>{challenge.description}</p>
            <pre>Sample Input: {challenge.sample_input}</pre>

            <form onSubmit={submit}>
                <textarea
                    value={output}
                    onChange={e => setOutput(e.target.value)}
                    rows="5"
                    placeholder="Enter your output"
                />
                <br />
                <button type="submit">Submit</button>
            </form>

            {message && <p className="feedback">{message}</p>}
        </div>
    );
}
