import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [attempts, setAttempts] = useState(0);
    const [cooldown, setCooldown] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (cooldown) {
            setErrorMessage('You are on cooldown. Please wait 30 seconds.');
            return;
        }
        try {
            const response = await axios.post('http://localhost:5000/login', { username, password });
            const token = response.data.access_token;
            localStorage.setItem('token', token); // Save token in local storage
            setErrorMessage(''); // Clear error message
            window.location.href = '/dashboard'; // Redirect to dashboard
        } catch (err) {
            setErrorMessage('Invalid credentials!');
            setAttempts((prev) => prev + 1); // Increment attempts
            if (attempts + 1 >= 3) {
                setCooldown(true); // Activate cooldown after 3 attempts
                setTimeout(() => {
                    setAttempts(0); // Reset attempts
                    setCooldown(false); // Disable cooldown
                }, 30000); // 30 seconds cooldown
            }
        }
    };

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                {errorMessage && <p style={{ color: 'red' }}>{errorMessage} (Attempts: {attempts}/3)</p>}
                <button type="submit" disabled={cooldown}>
                    {cooldown ? 'Cooldown...' : 'Login'}
                </button>
            </form>
        </div>
    );
};

export default Login;
