import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, Link } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Home from './pages/Home';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isLightMode, setIsLightMode] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsLoggedIn(true);
        }

        // Check saved light mode preference
        const savedMode = localStorage.getItem('theme');
        if (savedMode === 'light') {
            document.body.classList.add('light-mode');
            setIsLightMode(true);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
    };

    const toggleLightMode = () => {
        document.body.classList.toggle('light-mode');
        const newMode = document.body.classList.contains('light-mode');
        setIsLightMode(newMode);

        // Save the mode in localStorage
        localStorage.setItem('theme', newMode ? 'light' : 'dark');
    };

    return (
        <>
            {/* Header with Logo and Navbar */}
            <div className="header-logo">
                <Link to="/">
                    <img
                        src={isLightMode ? '/media/ninjatp.png' : '/media/ninjatpd.png'}
                        alt="Home"
                        className="ninja-logo"
                    />
                </Link>
            </div>

            <div className="navbar">
                <button
                    className="light-mode-btn"
                    onClick={toggleLightMode}
                >
                    {isLightMode ? 'Dark Mode' : 'Light Mode'}
                </button>
                <button
                    className="logout-btn"
                    onClick={handleLogout}
                >
                    Logout
                </button>
            </div>

            {/* App Routes */}
            <Routes>
                <Route path="/" element={<Home />} />
                <Route
                    path="/login"
                    element={isLoggedIn ? <Navigate to="/dashboard" /> : <Login setIsLoggedIn={setIsLoggedIn} />}
                />
                <Route
                    path="/register"
                    element={isLoggedIn ? <Navigate to="/dashboard" /> : <Register />}
                />
                <Route
                    path="/dashboard"
                    element={isLoggedIn ? <Dashboard handleLogout={handleLogout} /> : <Navigate to="/login" />}
                />
            </Routes>
        </>
    );
};

export default App;
