import React, { useEffect, useState } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import API from '../api';

export default function ChallengeDetail() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [chal, setChal] = useState(null);
    const [allChals, setAllChals] = useState([]);
    const [code, setCode] = useState('# write your solution here');
    const [runOut, setRunOut] = useState({ stdout: '', stderr: '' });
    const [submitMsg, setSubmitMsg] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [isRunning, setIsRunning] = useState(false);

    useEffect(() => {
        // load this challenge
        API.get(`/api/challenges/${id}`)
            .then(res => setChal(res.data))
            .catch(() => { /* ignore for now */ });

        // load list for Next button
        API.get('/api/challenges')
            .then(res => setAllChals(res.data));
    }, [id]);

    if (!chal) return <p>Loading challenge…</p>;

    // determine "next" challenge
    const idx = allChals.findIndex(c => c.id === chal.id);
    const next = idx >= 0 && idx < allChals.length - 1
        ? allChals[idx + 1]
        : null;

    const handleRun = async () => {
        setIsRunning(true);
        setRunOut({ stdout: 'Running…', stderr: '' });
        try {
            const r = await API.post('/api/run-challenge', {
                challenge_id: Number(id),
                code
            });
            setRunOut(r.data);
        } catch (e) {
            setRunOut({
                stdout: '',
                stderr: e.response?.data?.stderr || 'Error running code'
            });
        } finally {
            setIsRunning(false);
        }
    };

    const handleSubmit = async () => {
        if (isSubmitting) return;
        setIsSubmitting(true);
        setSubmitMsg('Running & submitting…');

        try {
            // first run
            const runRes = await API.post('/api/run-challenge', {
                challenge_id: Number(id),
                code
            });
            setRunOut(runRes.data);

            // then submit
            const output = runRes.data.stdout.trim();
            const subRes = await API.post('/api/submit-challenge', {
                challenge_id: Number(id),
                user_output: output
            });

            setSubmitMsg(
                `${subRes.data.message} XP: ${subRes.data.xp}, Rank: ${subRes.data.rank}`
            );

            // Refresh both the current challenge and the list
            const [chalRes, allChalsRes] = await Promise.all([
                API.get(`/api/challenges/${id}`),
                API.get('/api/challenges')
            ]);
            setChal(chalRes.data);
            setAllChals(allChalsRes.data);
        } catch (err) {
            const msg =
                err.response?.data?.message ||
                err.response?.data?.stderr ||
                'Error running or submitting.';
            setSubmitMsg(msg);
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="challenge-detail-page">
            <div className="challenge-detail-card">
                {/* ← Back / → Next nav */}
                <div style={{ marginBottom: '1rem' }}>
                    <Link to="/challenges">
                        <button className="enter-btn">Back to Challenges</button>
                    </Link>
                    {next && (
                        <Link to={`/challenges/${next.id}`}>
                            <button className="enter-btn" style={{ marginLeft: '1rem' }}>
                                Next Challenge
                            </button>
                        </Link>
                    )}
                </div>

                <h1>{chal.title}</h1>
                <p><strong>Input:</strong> {chal.sample_input}</p>

                {chal.completed ? (
                    <p className="success-message">
                        You've already completed this challenge.
                    </p>
                ) : (
                    <>
                        <Editor
                            height="60vh"
                            defaultLanguage="python"
                            value={code}
                            onChange={v => setCode(v)}
                            theme={
                                document.body.classList.contains('light-mode')
                                    ? 'light'
                                    : 'vs-dark'
                            }
                        />

                        <div style={{ margin: '1rem 0' }}>
                            <button
                                className="enter-btn"
                                onClick={handleRun}
                                disabled={isRunning}
                            >
                                {isRunning ? 'Running...' : 'Run'}
                            </button>
                            <button
                                className="enter-btn"
                                onClick={handleSubmit}
                                disabled={isSubmitting || chal.completed}
                                style={{ marginLeft: '1rem' }}
                            >
                                {isSubmitting ? 'Submitting...' : 'Submit'}
                            </button>
                        </div>

                        <div>
                            <h3>Output:</h3>
                            <pre style={{
                                background: '#111',
                                color: '#fff',
                                padding: '10px',
                                borderRadius: '5px'
                            }}>
                                {runOut.stdout}
                            </pre>
                            {runOut.stderr && (
                                <>
                                    <h3 style={{ color: 'red' }}>Errors:</h3>
                                    <pre style={{
                                        background: '#300',
                                        color: '#f88',
                                        padding: '10px',
                                        borderRadius: '5px'
                                    }}>
                                        {runOut.stderr}
                                    </pre>
                                </>
                            )}
                        </div>
                    </>
                )}

                {submitMsg && (
                    <p className={
                        submitMsg.startsWith('Correct') || submitMsg.includes('already completed')
                            ? 'success-message'
                            : 'error-message'
                    }>
                        {submitMsg}
                    </p>
                )}
            </div>
        </div>
    );
}
