import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ setIsLoggedIn }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [stayLoggedIn, setStayLoggedIn] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/login', {
                username,
                password,
            });
            const token = response.data.access_token;

            if (stayLoggedIn) {
                localStorage.setItem('token', token); // Save token in local storage
            } else {
                sessionStorage.setItem('token', token); // Save token in session storage
            }

            setErrorMessage('');
            setIsLoggedIn(true); // Update parent state to reflect login
        } catch (err) {
            setErrorMessage('Invalid credentials!');
        }
    };

    return (
        <div className="login-container">
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="username">Username:</label>
                    <input
                        type="text"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="password">Password:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <div className="checkbox-container">
                    <input
                        type="checkbox"
                        checked={stayLoggedIn}
                        onChange={(e) => setStayLoggedIn(e.target.checked)}
                        id="stay-logged-in"
                    />
                    <label htmlFor="stay-logged-in">Stay Logged In</label>
                </div>
                {errorMessage && <p className="error-message">{errorMessage}</p>}
                <button type="submit">Login</button>
            </form>
            <p>
                Don't have an account?{' '}
                <a className="switch-link" href="/register">
                    Register
                </a>
            </p>
        </div>
    );
};

export default Login;
