import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import API from '../api';

export default function ChallengeList() {
    const [challenges, setChallenges] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        API.get('/api/challenges')
            .then(res => setChallenges(res.data))
            .catch(err => {
                if (err.response?.status === 401) {
                    setError('Please log in again.');
                    setTimeout(() => navigate('/login'), 1500);
                } else {
                    setError('Failed to load challenges.');
                }
            });
    }, [navigate]);

    if (error) {
        return (
            <div className="dashboard-container">
                <p className="error-message">{error}</p>
            </div>
        );
    }
    if (!challenges.length) {
        return (
            <div className="dashboard-container">
                <p>Loading challengesâ€¦</p>
            </div>
        );
    }

    return (
        <div className="dashboard-container">
            <h1 className="dashboard-header">Challenges</h1>
            <div className="dashboard-content">
                {challenges.map(c => (
                    <div key={c.id} className="dashboard-card">
                        <h2>{c.title}</h2>
                        <p>Difficulty: {c.difficulty}</p>
                        <p>XP Reward: {c.xp_reward}</p>
                        <Link to={`/challenges/${c.id}`}>
                            <button className="enter-btn">Start</button>
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    );
}
