import React, { useEffect, useState } from 'react';
import API from '../api';

export default function Dashboard({ handleLogout }) {
    const [userData, setUserData] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        API.get('/api/user')
            .then(res => {
                setUserData(res.data);
                setError('');
            })
            .catch(err => {
                setError('Failed to load user data');
            })
            .finally(() => setIsLoading(false));
    }, []);

    if (isLoading) return <p>Loading dashboard...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div className="dashboard-container">
            <h1 className="dashboard-header">
                Welcome, <strong>{userData.username}</strong>
            </h1>

            <div className="dashboard-content">
                {/* Total XP */}
                <div className="dashboard-card">
                    <h2>Total XP Earned</h2>
                    <p><strong>{userData.xp}</strong></p>
                    <p>Rank: <strong>{userData.rank}</strong></p>
                </div>

                {/* Practice Log Summary */}
                <div className="dashboard-card" style={{ width: '100%', maxWidth: '600px' }}>
                    <h2>Practice Sessions</h2>
                    {userData.practice_summary.length === 0
                        ? <p>No practice sessions logged yet.</p>
                        : (
                            <ul style={{ textAlign: 'left' }}>
                                {userData.practice_summary.map(ps => (
                                    <li key={ps.challenge_id}>
                                        {ps.title}: <strong>{ps.runs}</strong> run{ps.runs !== 1 ? 's' : ''}
                                    </li>
                                ))}
                            </ul>
                        )}
                </div>
            </div>
        </div>
    );
}
