import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Home from './pages/Home';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const location = useLocation();

    useEffect(() => {
        // Check sessionStorage first, then localStorage
        const sessionToken = sessionStorage.getItem('token');
        const localToken = localStorage.getItem('token');

        if (sessionToken || localToken) {
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogout = () => {
        // Remove token from both storages
        localStorage.removeItem('token');
        sessionStorage.removeItem('token');
        setIsLoggedIn(false);
    };

    const shouldShowNavbar = location.pathname !== '/dashboard';

    return (
        <>
            {shouldShowNavbar && (
                <nav className="navbar">
                    <div className="nav-links">
                        {isLoggedIn && (
                            <button className="logout-btn" onClick={handleLogout}>
                                Logout
                            </button>
                        )}
                    </div>
                </nav>
            )}

            {location.pathname === '/dashboard' && isLoggedIn && (
                <button className="logout-btn dashboard-logout-btn" onClick={handleLogout}>
                    Logout
                </button>
            )}

            <Routes>
                <Route path="/" element={<Home />} />
                <Route
                    path="/login"
                    element={isLoggedIn ? <Navigate to="/dashboard" /> : <Login setIsLoggedIn={setIsLoggedIn} />}
                />
                <Route path="/register" element={isLoggedIn ? <Navigate to="/dashboard" /> : <Register />} />
                <Route path="/dashboard" element={isLoggedIn ? <Dashboard /> : <Navigate to="/login" />} />
            </Routes>
        </>
    );
};

export default App;
