import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        // Check if a token exists in localStorage to maintain login status
        const token = localStorage.getItem('token');
        if (token) {
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token'); // Clear the token
        setIsLoggedIn(false); // Update login state
    };

    return (
        <Router>
            <nav>
                <a href="/">Home</a>
                {!isLoggedIn && <a href="/login">Login</a>}
                {!isLoggedIn && <a href="/register">Register</a>}
                {isLoggedIn && <a href="/dashboard">Dashboard</a>}
                {isLoggedIn && <button onClick={handleLogout}>Logout</button>}
            </nav>
            <Routes>
                <Route
                    path="/"
                    element={
                        isLoggedIn ? <Navigate to="/dashboard" /> : <p>Welcome to Code Dojo!</p>
                    }
                />
                <Route
                    path="/login"
                    element={
                        isLoggedIn ? <Navigate to="/dashboard" /> : <Login setIsLoggedIn={setIsLoggedIn} />
                    }
                />
                <Route
                    path="/register"
                    element={
                        isLoggedIn ? <Navigate to="/dashboard" /> : <Register />
                    }
                />
                <Route
                    path="/dashboard"
                    element={
                        isLoggedIn ? <Dashboard /> : <Navigate to="/login" />
                    }
                />
            </Routes>
        </Router>
    );
};

export default App;
