import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import './styles.css'; // Import the CSS file

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        window.location.href = '/'; // Redirect to login after logout
    };

    return (
        <Router>
            <nav className="navbar">
                <div>
                    <a href="/" className="nav-link">Home</a>
                    {!isLoggedIn && <a href="/login" className="nav-link">Login</a>}
                    {!isLoggedIn && <a href="/register" className="nav-link">Register</a>}
                </div>
                {isLoggedIn && <button className="logout-button" onClick={handleLogout}>Logout</button>}
            </nav>

            <Routes>
                <Route
                    path="/"
                    element={isLoggedIn ? <Navigate to="/dashboard" /> : <Login setIsLoggedIn={setIsLoggedIn} />}
                />
                <Route
                    path="/login"
                    element={isLoggedIn ? <Navigate to="/dashboard" /> : <Login setIsLoggedIn={setIsLoggedIn} />}
                />
                <Route
                    path="/register"
                    element={isLoggedIn ? <Navigate to="/dashboard" /> : <Register />}
                />
                <Route
                    path="/dashboard"
                    element={isLoggedIn ? <Dashboard /> : <Navigate to="/login" />}
                />
            </Routes>
        </Router>
    );
};

export default App;
