import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [attempts, setAttempts] = useState(0);
    const [isCooldown, setIsCooldown] = useState(false);

    useEffect(() => {
        if (attempts >= 3) {
            setIsCooldown(true);
            setTimeout(() => {
                setAttempts(0);
                setIsCooldown(false);
            }, 30000); // 30-second cooldown
        }
    }, [attempts]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (isCooldown) return;

        try {
            const response = await axios.post('http://localhost:5000/login', { username, password });
            const token = response.data.access_token;
            localStorage.setItem('token', token); // Save the token in local storage
            setErrorMessage(''); // Clear any existing error message
            window.location.href = '/dashboard'; // Redirect to the Dashboard
        } catch (err) {
            setErrorMessage('Invalid credentials!');
            setAttempts((prev) => prev + 1); // Increment the attempts counter
        }
    };

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Username:
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </label>
                <br />
                <label>
                    Password:
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </label>
                <br />
                <button type="submit" disabled={isCooldown}>
                    Login
                </button>
            </form>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
            {isCooldown && <p style={{ color: 'orange' }}>Too many failed attempts. Please wait 30 seconds.</p>}
            <p>Failed Attempts: {attempts}</p>
        </div>
    );
};

export default Login;
