import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import API from '../api';

export default function ChallengeDetail() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [chal, setChal] = useState(null);
    const [code, setCode] = useState(`# write your solution here`);
    const [runOut, setRunOut] = useState({ stdout: '', stderr: '' });
    const [submitMsg, setSubmitMsg] = useState('');

    useEffect(() => {
        API.get(`/api/challenges/${id}`)
            .then(res => setChal(res.data))
            .catch(() => {
                // handle 401 or other errors if needed
            });
    }, [id]);

    const handleRun = () => {
        setRunOut({ stdout: 'Running…', stderr: '' });
        API.post('/api/run-challenge', { challenge_id: Number(id), code })
            .then(res => setRunOut(res.data))
            .catch(err =>
                setRunOut({
                    stdout: '',
                    stderr: err.response?.data?.stderr || 'Error running code'
                })
            );
    };

    const handleSubmit = () => {
        setSubmitMsg('Submitting…');
        API.post('/api/submit-challenge', {
            challenge_id: Number(id),
            user_output: runOut.stdout.trim()
        })
            .then(res =>
                setSubmitMsg(
                    `${res.data.message} XP: ${res.data.xp}, Rank: ${res.data.rank}`
                )
            )
            .catch(err =>
                setSubmitMsg(err.response?.data?.message || 'Submission error')
            );
    };

    if (!chal) return <p>Loading challenge…</p>;

    return (
        <div className="challenge-detail-page">
            <div className="challenge-detail-card">
                <h1>{chal.title}</h1>
                <p><strong>Input:</strong> {chal.sample_input}</p>

                <Editor
                    height="60vh"
                    defaultLanguage="python"
                    value={code}
                    onChange={v => setCode(v)}
                    theme={document.body.classList.contains('light-mode') ? 'light' : 'vs-dark'}
                />

                <div style={{ margin: '1rem 0' }}>
                    <button className="enter-btn" onClick={handleRun}>
                        Run
                    </button>
                    <button
                        className="enter-btn"
                        onClick={handleSubmit}
                        style={{ marginLeft: '1rem' }}
                    >
                        Submit
                    </button>
                </div>

                <div>
                    <h3>Output:</h3>
                    <pre style={{ background: '#111', color: '#fff', padding: '10px', borderRadius: '5px' }}>
                        {runOut.stdout}
                    </pre>
                    {runOut.stderr && (
                        <>
                            <h3 style={{ color: 'red' }}>Errors:</h3>
                            <pre style={{ background: '#300', color: '#f88', padding: '10px', borderRadius: '5px' }}>
                                {runOut.stderr}
                            </pre>
                        </>
                    )}
                </div>

                {submitMsg && (
                    <p className={submitMsg.startsWith('Correct') ? 'success-message' : 'error-message'}>
                        {submitMsg}
                    </p>
                )}
            </div>
        </div>
    );
}
