import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [attempts, setAttempts] = useState(0);
    const [isCooldown, setIsCooldown] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (isCooldown) return; // Prevent further attempts during cooldown

        try {
            const response = await axios.post('http://localhost:5000/login', { username, password });
            const token = response.data.access_token;
            localStorage.setItem('token', token); // Save token in local storage
            setErrorMessage(''); // Clear any error message
            setAttempts(0); // Reset attempts after successful login
            window.location.href = '/dashboard'; // Redirect to dashboard
        } catch (err) {
            setErrorMessage('Invalid credentials!');
            setAttempts((prev) => prev + 1); // Increment attempts

            if (attempts + 1 >= 3) {
                setIsCooldown(true); // Activate cooldown after 3 attempts
                setTimeout(() => {
                    setIsCooldown(false); // Reset cooldown after 30 seconds
                    setAttempts(0); // Reset attempts
                }, 30000); // 30-second cooldown
            }
        }
    };

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Username:
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </label>
                <label>
                    Password:
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </label>
                <button type="submit" disabled={isCooldown}>
                    {isCooldown ? 'Cooldown active...' : 'Login'}
                </button>
            </form>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
            <p>Attempts: {attempts}/3</p>
        </div>
    );
};

export default Login;
