import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../api';

export default function ChallengeDetail() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [challenge, setChallenge] = useState(null);
    const [output, setOutput] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        API.get(`/api/challenges/${id}`)
            .then(res => setChallenge(res.data))
            .catch(err => {
                if (err.response?.status === 401) {
                    setMessage('Please log in again.');
                    setTimeout(() => navigate('/login'), 1500);
                } else {
                    setMessage('Could not load challenge.');
                }
            });
    }, [id, navigate]);

    const handleSubmit = e => {
        e.preventDefault();
        setMessage('Submitting…');
        API.post('/api/submit-challenge', {
            challenge_id: Number(id),
            user_output: output
        })
            .then(res => {
                setMessage(`${res.data.message} XP: ${res.data.xp}, Rank: ${res.data.rank}`);
            })
            .catch(err => {
                const msg = err.response?.status === 401
                    ? 'Please log in again.'
                    : err.response?.data?.message || 'Error submitting.';
                setMessage(msg);
                if (err.response?.status === 401) {
                    setTimeout(() => navigate('/login'), 1500);
                }
            });
    };

    return (
        <div className="dashboard-container">
            {!challenge ? (
                <p>{message || 'Loading challenge…'}</p>
            ) : (
                <div className="dashboard-card">
                    <h1>{challenge.title}</h1>
                    <p><strong>Difficulty:</strong> {challenge.difficulty}</p>
                    <p><strong>XP Reward:</strong> {challenge.xp_reward}</p>
                    <p>{challenge.description}</p>
                    <pre>Sample Input: {challenge.sample_input}</pre>

                    <form onSubmit={handleSubmit}>
                        <textarea
                            rows="5"
                            value={output}
                            onChange={e => setOutput(e.target.value)}
                            placeholder="Enter your output here…"
                            style={{ width: '100%', padding: '10px', fontSize: '16px' }}
                        />
                        <br />
                        <button type="submit" className="enter-btn" style={{ marginTop: '10px' }}>
                            Submit
                        </button>
                    </form>

                    {message && (
                        <p className={message.startsWith('Please') ? 'error-message' : 'success-message'}>
                            {message}
                        </p>
                    )}
                </div>
            )}
        </div>
    );
}
